import React, { useState, useEffect } from 'react';
import UserProfile from '../components/UserProfile';
import UserRepositories from '../components/UserRepositories';
import UserFollowers from '../components/UserFollowers';
import UserFollowing from '../components/UserFollowing';
import UserContributions from '../components/UserContributions';
import { getUserProfile, getUserRepositories, getUserFollowers, getUserFollowing, getUserContributions } from '../services/githubService';
import { AppBar, Toolbar, Typography, Container, CssBaseline, CircularProgress, TextField, Button, Grid } from '@mui/material';

const HomePage = () => {
  const [username, setUsername] = useState('');
  const [profile, setProfile] = useState(null);
  const [repositories, setRepositories] = useState([]);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const [contributions, setContributions] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedUsername = localStorage.getItem('githubUsername');
    if (savedUsername) {
      setUsername(savedUsername);
      fetchData(savedUsername);
    }
  }, []);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleFetchData = () => {
    setLoading(true);
    fetchData(username);
  };

  const fetchData = async (username) => {
    try {
      const [profileData, repoData, followersData, followingData, contributionsData] = await Promise.all([
        getUserProfile(username),
        getUserRepositories(username),
        getUserFollowers(username),
        getUserFollowing(username),
        getUserContributions(username)
      ]);

      setProfile(profileData);
      setRepositories(repoData);
      setFollowers(followersData);
      setFollowing(followingData);
      setContributions(contributionsData);

      localStorage.setItem('githubUsername', username);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">
            Open Source Contribution Tracker
          </Typography>
        </Toolbar>
      </AppBar>
      <Container style={{ marginTop: '20px' }}>
        <TextField
          label="GitHub Username"
          value={username}
          onChange={handleUsernameChange}
          style={{ marginRight: '10px' }}
        />
        <Button variant="contained" color="primary" onClick={handleFetchData}>
          Fetch Data
        </Button>
        {loading ? (
          <CircularProgress style={{ marginTop: '20px' }} />
        ) : (
          profile && (
            <Grid container spacing={2} style={{ marginTop: '20px' }}>
              <Grid item xs={12}>
                <UserProfile profile={profile} />
              </Grid>
              <Grid item xs={12} sm={6}>
                <UserRepositories repositories={repositories} />
              </Grid>
              <Grid item xs={12} sm={6}>
                <UserFollowers followers={followers} />
              </Grid>
              <Grid item xs={12} sm={6}>
                <UserFollowing following={following} />
              </Grid>
              <Grid item xs={12}>
                <UserContributions contributions={contributions} />
              </Grid>
            </Grid>
          )
        )}
      </Container>
    </>
  );
};

export default HomePage;
