import axios from 'axios';

const GITHUB_API_URL = 'https://api.github.com';

export const getUserContributions = async (username) => {
  try {
    const reposResponse = await axios.get(`${GITHUB_API_URL}/users/${username}/repos`);
    const repos = reposResponse.data;

    const contributions = await Promise.all(
      repos.map(async (repo) => {
        const commitsResponse = await axios.get(
          `${GITHUB_API_URL}/repos/${username}/${repo.name}/commits?author=${username}`
        );
        return commitsResponse.data.map(commit => ({
          repo: repo.name,
          commit: commit.commit.message,
          date: commit.commit.author.date,
        }));
      })
    );

    return contributions.flat();
  } catch (error) {
    console.error('Error fetching user contributions:', error);
    throw error;
  }
};
