import axios from 'axios';

const GITHUB_API_URL = 'https://api.github.com';

export const getUserProfile = async (username) => {
  try {
    const response = await axios.get(`${GITHUB_API_URL}/users/${username}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user profile:', error);
    throw error;
  }
};

export const getUserRepositories = async (username) => {
  try {
    const response = await axios.get(`${GITHUB_API_URL}/users/${username}/repos`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user repositories:', error);
    throw error;
  }
};

export const getUserFollowers = async (username) => {
  try {
    const response = await axios.get(`${GITHUB_API_URL}/users/${username}/followers`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user followers:', error);
    throw error;
  }
};

export const getUserFollowing = async (username) => {
  try {
    const response = await axios.get(`${GITHUB_API_URL}/users/${username}/following`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user following:', error);
    throw error;
  }
};

export const getUserCommits = async (username, repo, page = 1) => {
  try {
    const response = await axios.get(
      `${GITHUB_API_URL}/repos/${username}/${repo}/commits?author=${username}`,
      { params: { page, per_page: 10 } }
    );
    return response.data.map(commit => ({
      repo,
      commit: commit.commit.message,
      date: commit.commit.author.date,
    }));
  } catch (error) {
    if (error.response && error.response.status === 409) {
      console.warn(`Conflict error for repository ${repo}`);
      return []; // Return an empty array if there's a conflict
    }
    throw error;
  }
};

export const getUserContributions = async (username, page = 1) => {
  try {
    const reposResponse = await getUserRepositories(username);
    const contributions = await Promise.all(
      reposResponse.map(async (repo) => {
        return await getUserCommits(username, repo.name, page);
      })
    );

    return contributions.flat();
  } catch (error) {
    console.error('Error fetching user contributions:', error);
    throw error;
  }
};
