import React, { useState, useEffect } from 'react';
import UserContributions from '../components/UserContributions';
import { getUserContributions } from '../services/githubService';
import { AppBar, Toolbar, Typography, Container, CssBaseline, CircularProgress } from '@mui/material';

const HomePage = () => {
  const [contributions, setContributions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContributions = async () => {
      const data = await getUserContributions('your-github-username');
      setContributions(data);
      setLoading(false);
    };
    fetchContributions();
  }, []);

  return (
    <>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">
            Open Source Contribution Tracker
          </Typography>
        </Toolbar>
      </AppBar>
      <Container>
        {loading ? (
          <CircularProgress />
        ) : (
          <UserContributions contributions={contributions} />
        )}
      </Container>
    </>
  );
};

export default HomePage;
