import React, { useState, useEffect } from 'react';
import UserContributions from '../components/UserContributions';
import { getUserContributions } from '../services/githubService';
import { AppBar, Toolbar, Typography, Container, CssBaseline, CircularProgress, TextField, Button } from '@mui/material';

const HomePage = () => {
  const [username, setUsername] = useState('');
  const [contributions, setContributions] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedUsername = localStorage.getItem('githubUsername');
    if (savedUsername) {
      setUsername(savedUsername);
      fetchContributions(savedUsername);
    }
  }, []);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleFetchContributions = () => {
    setLoading(true);
    fetchContributions(username);
  };

  const fetchContributions = async (username) => {
    try {
      const data = await getUserContributions(username);
      setContributions(data);
      localStorage.setItem('githubUsername', username);
    } catch (error) {
      console.error('Error fetching contributions:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">
            Open Source Contribution Tracker
          </Typography>
        </Toolbar>
      </AppBar>
      <Container style={{ marginTop: '20px' }}>
        <TextField
          label="GitHub Username"
          value={username}
          onChange={handleUsernameChange}
          style={{ marginRight: '10px' }}
        />
        <Button variant="contained" color="primary" onClick={handleFetchContributions}>
          Fetch Contributions
        </Button>
        {loading ? (
          <CircularProgress style={{ marginTop: '20px' }} />
        ) : (
          <UserContributions contributions={contributions} />
        )}
      </Container>
    </>
  );
};

export default HomePage;
